mapper.select:
SELECT recycle_time FROM snow.car_retreat_orders
  WHERE car_lease_id IN
  (SELECT id FROM snow.car_leases WHERE lease_number=#{leaseNumber});

SELECT remark FROM snow.car_retreat_orders
  WHERE car_lease_id  IN
  (SELECT id FROM snow.car_leases WHERE lease_number=#{leaseNumber});

mapper.update:
UPDATE snow.car_retreat_orders SET recycle_time=#{recycleTimeAfter},remark=#{remark}
  WHERE car_lease_id =
  (SELECT id FROM snow.car_leases WHERE lease_number=#{leaseNumber});

UPDATE snow.return_cash_pledge_orders SET recycle_time=#{recycleTimeAfter}
  WHERE car_lease_id =
  (SELECT id FROM snow.car_leases WHERE lease_number=#{leaseNumber});

mapper.select:
SELECT snow.car_register_infos.car_no FROM snow.car_leases
  JOIN snow.car_register_infos ON  snow.car_leases.car_register_info_id = snow.car_register_infos.id
WHERE lease_number=#{str};

SELECT snow.drivers.name FROM snow.car_leases
  JOIN snow.drivers ON  snow.car_leases.driver_id = snow.drivers.id
WHERE lease_number=#{str};
